// Dependencies

var
    babel       = require('gulp-babel'),
    gulp        = require('gulp'),
    sourcemaps  = require('gulp-sourcemaps');



// Tasks
gulp.task('default', function () {
    return gulp.src('src/*.js')
        .pipe(sourcemaps.init())
        .pipe(babel({ stage: 1 }))
        .pipe(sourcemaps.write())
        .pipe(gulp.dest('dist'));
});
